// Funcionamiento del selector

$(".default_option").click(function(){
  $(this).parent().toggleClass("active");
})

$(".select_ul li").click(function(){
  var currentele = $(this).html();
  $(this).parents(".select_wrap").children(".default_option").children("li").html(currentele);
  $(this).parents(".select_wrap").removeClass("active");
})



    $(document).click(function(){
    $(".default_option").parent().removeClass("active");
    // si lo deseamos podemos eliminar el evento click
    // una vez utilizado por primera vez
    // $(document).unbind("click");

    $(".default_option").click(function(event) {
    event.stopPropagation();
});
})
